import React, {useState} from 'react'

const Match = props => {
    
    const [score1, setScore1] = useState(0);

    const handleClick = (val, team) => e => {
        e.preventDefault();
        if(team==="teamA"){
            if(val==="+1") setScore1(score1=> score1 + 1)
            else setScore1(score1 => score1 + 3)
        }
        else{
            if(val==="+1") setScore2(score2=> score2 + 1)
            else setScore2(score2 => score2 + 3)
        }
        
    }
    return (
        <div className='container  d-flex justify-content-center' style={{ height:"100vh"}}>
           <div className='row mt-5 ' style={{width:"600px"}}>
               <div className='col-md-6 '>
                    <div className='card shadow p-2' style={{height:"400px"}}>
                        <h3>Team A: Dhirendra</h3>
                        <h5 className='text-center mt-3'>Score: 20</h5>
                        <div className='row mt-5'>
                            <div className='col-6'>
                                <button
                                    onClick={handleClick("+1", "teamA")} 
                                    className='btn btn-primary px-5'>+1</button>
                            </div>
                            <div className='col-6'>
                                <button className='btn btn-primary px-5'>+3</button>
                            </div>
                        </div>
                        <div className='d-flex justify-content-center mt-5'>
                            <button className='btn btn-primary px-3'>Team A Won</button>
                        </div>
                    </div>
               </div>
               <div className='col-md-6 '>
                    <div className='card shadow p-2' style={{height:"400px"}}>
                        <h3>Team B: Vikas</h3>
                        <h5 className='text-center mt-3'>Score: 20</h5>
                        <div className='row mt-5'>
                            <div className='col-6'>
                                <button className='btn btn-primary px-5'>+1</button>
                            </div>
                            <div className='col-6'>
                                <button className='btn btn-primary px-5'>+3</button>
                            </div>
                        </div>
                        <div className='d-flex justify-content-center mt-5'>
                            <button className='btn btn-primary px-3'>Team B Won</button>
                        </div>
                    </div>
               </div>
           </div>
        </div>
    )
}

export default Match;
