import React, {useEffect} from 'react'
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

import axios from 'axios';
import Matchlist from './components/MatchList';
import Match from './components/Match';
import { Provider } from 'react-redux';
import store from './redux/store';
const App = (props) => {

    useEffect(()=>{
        // getData()
        addData()
    },[])
    
    const getData = async () =>{
        const res = await axios.get('http://localhost:8003/matches');
        console.log("test res", res) 
    }
    const addData = async ()=>{
        const res =  await fetch("http://localhost:8003/match",{
            method: "POST",
            headers:{
               "Content-Type":"application/json"
            },
            body: JSON.stringify({
                team1Name:"test1", team2Name:"test2", team1Score:"21", team2Score:"23", winner:"test2"
            })
        })
        const data = await res.json();
        console.log(data);

    }

    return (
        <Provider store={store}>
            <div>
            <Router>
                <Switch>
                    <Route exact path="/matches" component={Matchlist}/>
                    <Route exact path="/match" component={Match}/>
                </Switch>
            </Router>
        </div>
        </Provider>
    )
}
export default App;
